SELECT TITLE, used_goods_board.board_id BOARD_ID, REPLY_ID, used_goods_reply.writer_id WRITER_ID, used_goods_reply.contents CONTENTS, date_format(used_goods_reply.CREATED_DATE, '%Y-%m-%d') CREATED_DATE from used_goods_board inner join used_goods_reply on used_goods_board.board_id = used_goods_reply.board_id where year(used_goods_board.created_date) = 2022 and month(used_goods_board.created_date) = 10 order by used_goods_reply.created_date, title;